    // Registration of item blocks.

    private static Item register(String ID, BlockItem Block)
    {
        if (DebugClient)
        {
            ConsoleClient.info("[" + NameClient + "] Item (" + ID + ") : OK !");
        }
        return Registry.register(Registry.ITEM, new Identifier(NameClient.toLowerCase(), ID), Block);
    }

    // Registration of items.

    private static Item register(String ID, Item Item)
    {
        if (DebugClient)
        {
            ConsoleClient.info("[" + NameClient + "] Item (" + ID + ") : OK !");
        }
        return Registry.register(Registry.ITEM, new Identifier(NameClient.toLowerCase(), ID), Item);
    }
